mapper.select:
/* 영양정보 등록 요청 조회 */
SELECT nirr_num, m.mbr_name, nirr_content, nirr_date, nirr.nirr_category, sc.sc_status, prr_admin, nirr_processing_date, nirr_reason_content FROM nutrition_info_registration_request nirr INNER JOIN `member` m ON nirr.mbr_id = m.mbr_id INNER JOIN status_category sc ON nirr.sc_code = sc.sc_code
1=1
AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND nirr_processing_date > #{startDate}
AND nirr_processing_date < #{endDate}
ORDER BY nirr.nirr_num LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

/* 멤버 총개수 */ SELECT count(*) FROM nutrition_info_registration_request nirr INNER JOIN `member` m ON nirr.mbr_id = m.mbr_id INNER JOIN status_category sc ON nirr.sc_code = sc.sc_code
1=1
AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND nirr_processing_date > #{startDate}
AND nirr_processing_date < #{endDate}
ORDER BY nirr.nirr_num

mapper.update:
UPDATE nutrition_info_registration_request
nirr_num = #{nirrNum}, nirr_processing_date = now(), sc_code = 'sc_003'
WHERE nirr_num = #{nirrNum};

UPDATE nutrition_info_registration_request
nirr_num = #{nirrNum}, nirr_processing_date = now(), sc_code = 'sc_003'
WHERE nirr_num = #{nirrNum};

mapper.select:
SELECT mni_num, mni_name, mni_picture, mni_weight, mni_kcal, mni_carbo, mni_protein, mni_fat, mni_sugar, mni_cholesterol, mni_sodium, mni_calcium, mni_iron FROM meal_nutrition_info
AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND pumh_date > #{startDate}
AND pumh_date < #{endDate}
ORDER BY mni_num LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

	/* 멤버 총개수 */
	SELECT 
		count(*)
	FROM meal_nutrition_info
	
		1=1
		
	        AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%') 
	    
	    
	        AND pumh_date > #{startDate}
	    
	    
	        AND pumh_date  <  #{endDate}
	    			
	
    ORDER BY
    	mni_num

SELECT dsi_num, dsb_num, dsi_name, dsi_efficacy, dsi_picture, dsi_daily_recommended_intake FROM dietary_supplement_info
AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND pumh_date > #{startDate}
AND pumh_date < #{endDate}
ORDER BY dsi_num LIMIT #{pageable.rowPerPage} OFFSET #{pageable.offset};

	/* 멤버 총개수 */
	SELECT 
		count(*)
	FROM dietary_supplement_info
	
		1=1
		
	        AND ${searchType} LIKE CONCAT('%', #{searchKeyword}, '%') 
	    
	    
	        AND pumh_date > #{startDate}
	    
	    
	        AND pumh_date  <  #{endDate}
	    			
	
    ORDER BY
    	dsi_num
