mapper.select:
  		SELECT COUNT(*)
		FROM free_board
		ORDER BY fb_date DESC;

  		SELECT fbc_num, mbr_id, fbc_content, fbc_date
		FROM free_board_comments
		WHERE fb_num=#{articleKeyNum}

mapper.update:
  	UPDATE free_board
	SET fb_like=${calcLikeCnt}
	WHERE fb_num =#{likeOccurArticleNumValue}
