mapper.select:
SELECT f.feed_num, f.mbr_id, f.mni_num, f.feed_food_intake, f.feed_content, f.mc_num, f.feed_date, f.feed_intake_date, f.feed_like, f.feed_open_status, fi_feed.file_path AS feed_file_path, fi_feed.file_new_name AS feed_file_name, fi_member.file_path AS member_file_path, fi_member.file_new_name AS member_file_name, mll.mni_name, mll.mni_picture, mll.mni_weight, mll.mni_kcal, mll.mni_carbo, mll.mni_protein, mll.mni_fat, mll.mni_sugar, mll.mni_cholesterol, mll.mni_sodium, mll.mni_calcium, mll.mni_iron, fc.fc_content
FROM feed f INNER JOIN meal_nutrition_info mll ON f.mni_num = mll.mni_num LEFT JOIN files fi_feed ON f.feed_file_index = fi_feed.file_idx LEFT JOIN member m ON f.mbr_id = m.mbr_id LEFT JOIN files fi_member ON m.mbr_file_idx = fi_member.file_idx LEFT JOIN feed_comments fc ON f.feed_num = fc.feed_num
ORDER BY f.feed_date DESC;

mapper.delete:
	    DELETE
	    FROM
	    	feed_comments
	    WHERE
	    	feed_num = #{feedCode}
    	AND mbr_id = #{memberId}

    DELETE
    FROM
    	files
    WHERE
    	file_idx = (
		SELECT feed_file_index
		FROM feed
		WHERE feed_num = #{feedCode}
	);

	    DELETE
    	FROM
    		feed
	    WHERE
	    	feed_num = #{feedCode}
    	AND mbr_id = #{memberId}

mapper.update:
UPDATE feed_comments
SET
	fc_content=#{feedCommentContent}
WHERE
	fc_num=#{feedCommentCode}
