mapper.update:
UPDATE member
SET mbr_pw = #{newMemberPw}
WHERE mbr_id = #{memberId}

   UPDATE member 

    
     mbr_pw = #{newMemberPw},
    
     mbr_phone_num = #{memberPhoneNum},
    
     mbr_email = #{memberEmail}

   WHERE mbr_id = #{memberId};

mapper.select:
SELECT ctph.mbr_id, ctph.ctph_date, ctc.ctc_category, pmc.pmc_name, ctm.ctm_date, ctm.ctm_return_status, COUNT(ctm.ctm_date) OVER () AS ticketUsedCnt, COUNT(*) OVER () - COUNT(ctm.ctm_date) OVER () AS ticketNotUsedCnt FROM challenge_ticket_payment_history ctph INNER JOIN challenge_ticket_price ctp USING(ctp_code) INNER JOIN challenge_ticket_category ctc USING(ctc_code) INNER JOIN ticket_payment_method tpm USING(tpm_num) INNER JOIN payment_method_category pmc USING(pmc_code) INNER JOIN challenge_ticket_management ctm USING(ctph_num) WHERE ctph.mbr_id = #{memberId}
AND ctph.ctph_date >= #{startDate}
AND ctph.ctph_date <= #{endDate}
ORDER BY ctph_date DESC LIMIT #{rowPerPage} OFFSET #{offset};

   		SELECT
			COUNT(*)
		FROM
			challenge_ticket_payment_history
		where
			mbr_id = #{memberId}
			
			AND ctph_date >= #{startDate} 
			
			
			AND ctph_date  <=  #{endDate}
